package com.vannezha.gencharimport android.content.Intentimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.ImageViewimport android.widget.TextViewimport androidx.recyclerview.widget.RecyclerViewimport com.bumptech.glide.Glideimport com.bumptech.glide.request.RequestOptionsclass CardViewHeroAdapter(private val listGenchar: ArrayList<Genchar>) : RecyclerView.Adapter<CardViewHeroAdapter.CardViewViewHolder>() {    class CardViewViewHolder(itemView: View, listener: OnItemClickListener) : RecyclerView.ViewHolder(itemView) {        var imgPhoto: ImageView = itemView.findViewById(R.id.img_item_photo)        var tvName: TextView = itemView.findViewById(R.id.tv_item_name)        var tvDetail: TextView = itemView.findViewById(R.id.tv_item_detail)        var imgElement: ImageView = itemView.findViewById(R.id.img_item_element)        var imgStars: ImageView = itemView.findViewById(R.id.img_item_stars)        init {            itemView.setOnClickListener {                listener.onItemClick(adapterPosition)            }        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): CardViewViewHolder {        val view: View = LayoutInflater.from(parent.context).inflate(R.layout.item_cardview_genchar, parent, false)        return CardViewViewHolder(view,mlistener)    }    override fun getItemCount(): Int {        return listGenchar.size    }    override fun onBindViewHolder(holder: CardViewViewHolder, position: Int) {        val genchar = listGenchar[position]        Glide.with(holder.itemView.context)            .load(genchar.photo)            .apply(RequestOptions().override(350, 550))            .into(holder.imgPhoto)        Glide.with(holder.itemView.context)            .load(genchar.rarity)            .apply(RequestOptions().override(350, 550))            .into(holder.imgStars)        Glide.with(holder.itemView.context)            .load(genchar.vision)            .apply(RequestOptions().override(350, 550))            .into(holder.imgElement)        holder.tvName.text = genchar.name        holder.tvDetail.text = genchar.description    }    interface OnItemClickListener{        fun onItemClick(position: Int)    }    private lateinit var mlistener: OnItemClickListener    fun setOnItemClickListener(listener: OnItemClickListener){        this.mlistener = listener    }}